CREATE TABLE IF NOT EXISTS retention_history (customer_id BIGINT PRIMARY KEY NOT NULL DEFAULT 0, credit_score BIGINT NOT NULL DEFAULT 0, country VARCHAR(50) NOT NULL, gender VARCHAR(50) NOT NULL, age BIGINT NOT NULL DEFAULT 0, tenure BIGINT NOT NULL DEFAULT 0, balance BIGINT NOT NULL DEFAULT 0, products_number BIGINT NOT NULL DEFAULT 0, credit_card BIGINT NOT NULL DEFAULT 0, active_member BIGINT NOT NULL DEFAULT 0, estimated_salary BIGINT NOT NULL DEFAULT 0, churn BIGINT NOT NULL DEFAULT 0 );